    private void botaoCarregarDadosActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botaoCarregarDadosActionPerformed
     try {
         Controlador.Empresa.carregar(this.emp);
         JOptionPane.showMessageDialog(null, "Dados Carregados!", "Sucesso", JOptionPane.WARNING_MESSAGE);
     } catch (IOException | ClassNotFoundException ex) {
         JOptionPane.showMessageDialog(null, "Dados não carragados, tente novamente!", "Erro", JOptionPane.ERROR_MESSAGE);
         Logger.getLogger(TelaPrincipal.class.getName()).log(Level.SEVERE, null, ex);
     }

    }//GEN-LAST:event_botaoCarregarDadosActionPerformed

    private void botaoCadastrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botaoCadastrarActionPerformed
        //botao cadastrar pressionado

        Object[] opcoes = {"Clientes", "Produtos"};
        Object selectedValue; //
        selectedValue = JOptionPane.showInputDialog(null, "Selecione o que deseja cadastrar", "Opções",
                JOptionPane.INFORMATION_MESSAGE, null, opcoes, opcoes[0]);
        this.setEnabled(false);
        //   System.out.println(selectedValue);
        if (selectedValue == null) {
            this.setEnabled(true);
        } else if (selectedValue.equals("Clientes")) {
            TelaCadastroCliente telinhaCadastroCliente = new TelaCadastroCliente(this, emp);
            telinhaCadastroCliente.setVisible(true);
        } else if (selectedValue.equals("Produtos")) {
            TelaCadastroProdutos telinhaCadastroProduto = new TelaCadastroProdutos(this, emp);
            telinhaCadastroProduto.setVisible(true);
        }

    }//GEN-LAST:event_botaoCadastrarActionPerformed

