    private void botaoSalvarDadosActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botaoSalvarDadosActionPerformed
     try {
         Controlador.Empresa.salvar(this.emp);
         JOptionPane.showMessageDialog(null, "Dados foram salvos!", "Sucesso", JOptionPane.WARNING_MESSAGE);
     } catch (IOException ex) {
         JOptionPane.showMessageDialog(null, "Dados não foram salvos, tente novamente!", "Erro", JOptionPane.ERROR_MESSAGE);
         Logger.getLogger(TelaPrincipal.class.getName()).log(Level.SEVERE, null, ex);
     }
    }//GEN-LAST:event_botaoSalvarDadosActionPerformed

    private void botaoRelatoriosActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_botaoRelatoriosActionPerformed
       //botao cadastrar pressionado

        Object[] opcoes = {"Clientes",
                                          "Produtos",
                                          "Vendas"};
        Object selectedValue; //
        selectedValue = JOptionPane.showInputDialog(null, "Selecione o relatórios desejado", "Opções",
                JOptionPane.INFORMATION_MESSAGE, null, opcoes, opcoes[0]);
        this.setEnabled(false);
        //   System.out.println(selectedValue);
        if (selectedValue == null) {
            this.setEnabled(true);
        } else if (selectedValue.equals("Clientes")) {
            TelaRelatorioCliente telinhaRelatorioClientes = new TelaRelatorioCliente(this, emp);
            telinhaRelatorioClientes.setVisible(true);
        } else if (selectedValue.equals("Produtos")) {
            TelaRelatorioProduto telinhaRelatorioProdutos = new TelaRelatorioProduto(this, emp);
            telinhaRelatorioProdutos.setVisible(true);
        }
        else if (selectedValue.equals("Vendas")) {
            TelaRelatorioVendas telinhaRelatorioVendas = new TelaRelatorioVendas(this, emp);
            telinhaRelatorioVendas.setVisible(true);
        }
        else if (selectedValue.equals("Cliente Específico")) {
            TelaRelatorioClienteEspecifico telinhaRelatorioClienteEspecifico = new TelaRelatorioClienteEspecifico(this, emp);
            telinhaRelatorioClienteEspecifico.setVisible(true);
        }
    }//GEN-LAST:event_botaoRelatoriosActionPerformed

